INSERT INTO `wp_snippets` VALUES (6,'Redirect guests to Members hub','','\n// 未ログインが「公開を許したURL」以外を開いたら /members/ へ寄せる\nadd_action(\'template_redirect\', function () {\n\n    // ログイン or 管理/特殊リクエストは除外\n    if ( is_user_logged_in() ) return;\n    if ( is_admin() || wp_doing_ajax() || ( defined(\'REST_REQUEST\') && REST_REQUEST ) ) return;\n    if ( is_feed() || is_trackback() || is_preview() ) return;\n\n    // 緊急退避（?bypass=1 で一時停止）\n    if ( isset($_GET[\'bypass\']) && $_GET[\'bypass\'] === \'1\' ) return;\n\n    // リクエストされたパスを正規化（例：/foo → /foo/）\n    $path = parse_url( $_SERVER[\'REQUEST_URI\'], PHP_URL_PATH );\n    $path = rtrim( $path, \'/\' ) . \'/\';\n\n    // ここに「未ログインでも見せるURL」を列挙\n    $allow = [\n        \'/\',                    // トップ\n        \'/wp-login.php\',        // WPログイン\n        \'/member-register/\',    // 会員登録\n        \'/members/\',            // 会員ハブ（ログイン促し用にも使う）\n        \'/privacy-policy/\',\n        \'/sample-page/\',\n		\'/property_list/\',		// 物件一覧\n    ];\n\n    // 静的ファイル/システム系は素通し\n    if (\n        str_starts_with($path, \'/wp-admin/\')   ||\n        str_starts_with($path, \'/wp-includes/\')||\n        str_starts_with($path, \'/wp-content/\') ||\n        str_starts_with($path, \'/robots.txt\')  ||\n        str_starts_with($path, \'/sitemap\')     ||\n        str_starts_with($path, \'/wp-json/\')\n    ) {\n        return;\n    }\n\n    // 開発用：テーマやプラグインから許可URLを追加できるフック\n    $allow = apply_filters(\'my_members_allowlist_paths\', $allow);\n\n    // 許可URLなら表示\n    if ( in_array( $path, $allow, true ) ) return;\n\n    // ここからリダイレクト判定\n    // 単一ページ/投稿/物件（CPT）や物件アーカイブは会員化\n    if ( is_singular( [\'post\',\'page\',\'property\'] ) || is_post_type_archive(\'property\') ) {\n        wp_safe_redirect( site_url(\'/members/\') );\n        exit;\n    }\n\n    // その他の画面（カテゴリ一覧など）も原則寄せる場合は下を有効化\n    // wp_safe_redirect( site_url(\'/members/\') ); exit;\n});\n','','front-end',0,10,1,'2025-11-07 00:56:11',1,NULL);
INSERT INTO `wp_snippets` VALUES (7,'After login/logout redirect to Members','','\n// ログイン後は /members/ へ\nadd_filter(\'wpmem_login_redirect\', function( $redirect_to, $user_id ){\n    return site_url(\'/members/\');\n}, 10, 2);\n\n// ログアウト後はトップへ（好みで変更可）\nadd_filter(\'wpmem_logout_redirect\', function( $redirect_to ){\n    return home_url(\'/\'); // 例：site_url(\'/member-register/\') にしてもOK\n}, 10, 1);\n','','front-end',0,10,1,'2025-10-29 05:54:57',1,NULL);
INSERT INTO `wp_snippets` VALUES (8,'Member consult form (shortcode + handler)','','add_filter(\n    \'ovl/member_consult_redirect\',\n    function( $redirect, $status, $extra ) {\n        if ( \'ok\' === $status && ! empty( $extra[\'post_id\'] ) ) {\n            return add_query_arg(\n                \'post_id\',\n                absint( $extra[\'post_id\'] ),\n                home_url( \'/member-consult-thank-you/\' )\n            );\n        }\n\n        return $redirect; // それ以外は前段（mu-plugins側）の戻り値をそのまま返す\n    },\n    5, // ← 優先度を mu-plugins (10) より小さくするか、逆に大きくしてもOK\n    3\n);','','global',0,10,1,'2025-11-07 06:27:22',1,NULL);
INSERT INTO `wp_snippets` VALUES (9,'Register consult CPT','','\n/**\n * CPT: consult / property 登録 + 権限付与（冪等）\n * - consult: 管理画面のみ、専用capで安全運用（map_meta_cap有効）\n * - property: 公開アーカイブあり（/property/）\n */\n\nadd_action(\'init\', function () {\n\n  // ---- consult のcap定義（primitive caps）\n  $caps = [\n    \'edit_post\'              => \'edit_consult\',\n    \'read_post\'              => \'read_consult\',\n    \'delete_post\'            => \'delete_consult\',\n\n    \'edit_posts\'             => \'edit_consults\',\n    \'edit_others_posts\'      => \'edit_others_consults\',\n    \'publish_posts\'          => \'publish_consults\',\n    \'read_private_posts\'     => \'read_private_consults\',\n\n    \'create_posts\'           => \'create_consults\',\n    \'delete_posts\'           => \'delete_consults\',\n    \'delete_private_posts\'   => \'delete_private_consults\',\n    \'delete_published_posts\' => \'delete_published_consults\',\n    \'delete_others_posts\'    => \'delete_others_consults\',\n    \'edit_private_posts\'     => \'edit_private_consults\',\n    \'edit_published_posts\'   => \'edit_published_consults\',\n  ];\n\n  // ---- consult（管理画面のみ表示）\n  register_post_type(\'consult\', [\n    \'label\'               => \'Consults\',\n    \'labels\'              => [\'name\' => \'Consults\', \'singular_name\' => \'Consult\'],\n    \'public\'              => false,             // 公開しない\n    \'publicly_queryable\'  => false,             // 個別URLでの参照も不可\n    \'exclude_from_search\' => true,\n    \'show_ui\'             => true,              // 管理画面に表示\n    \'show_in_menu\'        => true,\n    \'show_in_rest\'        => false,             // 必要なら true（Gutenberg/REST）\n    \'supports\'            => [\'title\',\'editor\',\'custom-fields\',\'author\'],\n    \'menu_position\'       => 25,\n    \'menu_icon\'           => \'dashicons-analytics\',\n\n    \'map_meta_cap\'        => true,              // meta→primitive capに解決\n    \'capability_type\'     => [\'consult\',\'consults\'],\n    \'capabilities\'        => $caps,\n  ]);\n\n  // ---- property（公開アーカイブ）\n  register_post_type(\'property\', [\n    \'label\'               => \'Properties\',\n    \'labels\'              => [\'name\' => \'Properties\', \'singular_name\' => \'Property\'],\n    \'public\'              => true,\n    \'publicly_queryable\'  => true,\n    \'exclude_from_search\' => false,\n    \'show_ui\'             => true,\n    \'show_in_rest\'        => true,\n    \'has_archive\'         => true,                        // /property/\n    \'rewrite\'             => [\'slug\' => \'property\', \'with_front\' => false],\n    \'supports\'            => [\'title\',\'editor\',\'thumbnail\',\'excerpt\'],\n    \'menu_position\'       => 5,\n    \'menu_icon\'           => \'dashicons-building\',\n  ]);\n\n  // ---- 権限付与（冪等）\n  if ( $role = get_role(\'administrator\') ) {\n    foreach ($caps as $cap) { $role->add_cap($cap); }\n    // 念のため基本も\n    $role->add_cap(\'read\');\n    $role->add_cap(\'edit_consult\');\n    $role->add_cap(\'read_consult\');\n    $role->add_cap(\'delete_consult\');\n  }\n\n  // 会員ロール：あれば member、無ければ subscriber をベースに\n  $member_role = get_role(\'member\') ?: get_role(\'subscriber\');\n  if ( $member_role ) {\n    // 最低限：作成（フロント投稿用）と read\n    $member_role->add_cap(\'create_consults\');\n    $member_role->add_cap(\'read\');\n\n    // もし「自分の下書き/公開済みを編集できる」挙動にしたい場合は下を有効化\n    // $member_role->add_cap(\'edit_consults\');\n    // $member_role->add_cap(\'edit_published_consults\');\n    // $member_role->add_cap(\'delete_consults\');\n  }\n\n}, 5); // ← 早めに登録（rewrite衝突を避ける）\n','','global',0,10,1,'2025-11-02 03:21:21',1,NULL);
INSERT INTO `wp_snippets` VALUES (11,'Redirect Property Detai','','// Membersプラグインの強制リダイレクトを無効化\nremove_filter( \'login_redirect\', \'members_login_redirect\', 9 );\n\n// login_redirect（WPコア）で redirect_to を優先\nadd_filter( \'login_redirect\', function( $redirect_to, $request, $user ) {\n	if ( empty( $user ) || is_wp_error( $user ) ) {\n		return $redirect_to;\n	}\n	if ( empty( $_REQUEST[\'redirect_to\'] ) ) {\n		return $redirect_to;\n	}\n	$target = wp_validate_redirect( wp_unslash( $_REQUEST[\'redirect_to\'] ), home_url( \'/members/\' ) );\n	return $target ?: $redirect_to;\n}, 99, 3 );\n\n// wpmem_login_redirect（WP-Members独自）でも同じ値を返す\nadd_filter( \'wpmem_login_redirect\', function( $redirect_to, $user_id ) {\n	if ( empty( $_REQUEST[\'redirect_to\'] ) ) {\n		return $redirect_to;\n	}\n	$target = wp_validate_redirect( wp_unslash( $_REQUEST[\'redirect_to\'] ), home_url( \'/members/\' ) );\n	return $target ?: $redirect_to;\n}, 99, 2 );','','global',0,10,1,'2025-11-09 02:29:06',1,NULL);
